import React from "react";
import MarkdownEditor from "./MarkdownEditor";
class App extends React.Component {
    constructor(props) {
        super(props);
        this.props = props;
        this.props.type = String;
    }
    static getDerivedStateFromError(error) {
        // Update state so the next render will show the fallback UI.
        return { hasError: true };
    }
    sendErrorStatistics() {
        window.opener = null;
        window.open("https://github.com/yoyomonem/jsx-md-edit/issues/new?assignees=yoyomonem&labels=bug&template=bug_report.md&title=%5BREACT+BUG%5D+The+React+app+itself");
    }
    componentDidCatch(error, errorInfo) {
        sendErrorStatistics();
    }
    render() {
        if (this.state.hasError) {
                return (
                    <div>
                        <h3>Something went wrong.</h3>
                        <div id="actualResult">
                            <h1>Actual result:</h1>
                            <h1>Markdown Editor (React JSX)</h1>
                            <MarkdownEditor />
                            <p>Type: {this.props.attr.type}</p>
                        </div>
                    </div>
                );
        }
        return (
            <div>
                <h1>Markdown Editor (React JSX)</h1>
                <MarkdownEditor />
                <p>Type: {this.props.attr.type}</p>
            </div>
        );
    }
}
export default App;
